/**
 * This class will take wheels generated by RandomiseWheels and test if they have a win line or not
 * For time being win line will mean 2 or more of the same symbols in a row anywhere on a wheel
 * 
 *  (x,x,x,x,x) = biggest win
 *  (x,x,x,x,Y) OR (Y,x,x,x,x,)= 2nd biggest win
 * 	(x,x,x,Y,W) OR (Y,x,x,x,Y) OR (Y,W,x,x,x) = third biggest win
 *  (x,x,Y,W,Z) OR (Y,x,x,Y,Z) OR (Y,Z,x,x,Y) OR (Y,W,Z,x,x) = smallest win
 * 
 */


/**
 * @author mikey
 *
 */
public class CheckWheels {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method

	}
	
	public static void checkWheel(String[] wheel){		
		int k = 0;
		int numOfMatches = 0;
		//Matches five points
		for(int i = 0; i <= wheel.length-1; i++){
			//cahnge shit
			if(wheel[k].equals(wheel[k+1])){
				numOfMatches++;
			}
			k++;
		}
		
		switch(numOfMatches){
		case 0:
			System.out.println("No matches! unlucky..");
			break;
		case 1:
			System.out.println("A match! small win");
			break;
		case 2: 
			System.out.println("Two matches! big win");
			break;
		case 3: 
			System.out.println("Three of a king! bigger win!");	
			break;
		case 4:
			System.out.println("Four of a kind! Even bigger win!");
			break;
		
		}		
		
	}
	

}
